mapper.select:
SELECT t.id, DATE_FORMAT( t.createTime, '%Y-%m-%d %H:%i:%s' ) AS createTime, DATE_FORMAT( t.updateTime, '%Y-%m-%d %H:%i:%s' ) AS updateTime, t.targetName, t.targetMonth, GROUP_CONCAT(a.RealName SEPARATOR '„ÄÅ') AS userName, GROUP_CONCAT(a.AccountID) AS userId FROM ( SELECT t1.*, indicatorUserId FROM t_target t1 LEFT JOIN t_indicator_configuration t2 ON t2.targetId = t1.id WHERE 1 = 1
AND t2.indicatorUserId = #{userId}
GROUP BY t1.id ) t LEFT JOIN ( SELECT indicatorUserId, targetId FROM t_indicator_configuration WHERE indicatorUserId IS NOT NULL GROUP BY indicatorUserId, targetId ) c ON c.targetId = t.id LEFT JOIN hrm_db.t_hrm_accountinfo a ON a.AccountID = c.indicatorUserId WHERE t.businessCode = #{businessCode} AND ifTarget = '0'
AND t.targetMonth = #{targetMonth}
GROUP BY t.id ORDER BY t.targetMonth DESC

SELECT
t.id
FROM
t_target t
LEFT JOIN t_indicator_configuration c ON c.targetId = t.id
WHERE c.indicatorUserId IN

    #{item}

     AND t.businessCode = #{businessCode} AND t.ifTarget = '0' AND t.targetMonth = #{targetMonth}

    AND c.targetId != #{id}

LIMIT 1

mapper.update:
UPDATE t_target

    updateTime = NOW(),

        targetName = #{targetName}


WHERE
    id = #{id}
